using System.Diagnostics;
using System.Windows;

namespace AdventOfCode
{
    class Day2
    {
        public static void DoIt()
        {
            string input =
                            //                @"ULL
                            //RRDDD
                            //LURDL
                            //UUUUD";
            @"
            
            
            
            ";

            string result = "";

            string keypad = @"    1
  2 3 4
5 6 7 8 9
  A B C
    D";
            // Part 1
            //            string keypad = @"1 2 3
            //4 5 6
            //7 8 9";

            var kl = keypad.Split('\n');

            // 1,1 for part 1
            int x = 0;
            int y = 2;
            foreach (var line in input.Split('\n'))
            {

                foreach (var chr in line)
                {
                    if (chr == 'U' && IsGood(kl, x, y - 1))
                        y--;
                    if (chr == 'D' && IsGood(kl, x, y + 1))
                        y++;
                    if (chr == 'L' && IsGood(kl, x - 1, y))
                        x--;
                    if (chr == 'R' && IsGood(kl, x + 1, y))
                        x++;
                }

                char codeDigit = kl[y][2*x];
                Debug.WriteLine(codeDigit);

                result += codeDigit;

            }

            Clipboard.SetText(result);
        }

        private static bool IsGood(string[] kl, int x, int y)
        {
            if (x < 0 || y < 0)
                return false;

            if (y >= kl.Length )
                return false;

            if (x * 2 >= kl[y].Length)
                return false;
            return kl[y][2*x] != ' ';
        }

 
    }
}